// let's imagine this file is autogenerated from the backend
// ideally, we want to keep these api related types in sync
// with the backend instead of manually writing them out

export type BaseEntity = {
  id: string;
  createdAt: number;
};

export type Entity<T> = {
  [K in keyof T]: T[K];
} & BaseEntity;

export type Meta = {
  page: number;
  total: number;
  totalPages: number;
};

export type Team = Entity<{
  name: string;
  description: string;
}>;

export type Discussion = Entity<{
  title: string;
  body: string;
  teamId: string;
  author: User;
}>;

export type Comment = Entity<{
  body: string;
  discussionId: string;
  author: User;
}>;

export interface User {
  user_id: number;
  email: string;
  token: string;
}

export interface AuthResponse {
  user: User;
  token: string;
}

export interface ApiResponse<T> {
  success: boolean;
  meta: {
    timestamp: string;
    request_id: string;
    version: string;
  };
  status: number;
  data: T;
  error?: {
    code: string;
    message: string;
    details?: string;
  };
  links?: {
    self: string;
    [key: string]: string;
  };
  pagination?: {
    count: number;
    next: string | null;
    previous: string | null;
  };
}
